(function($){function triggerChange(checkbox,target){var isChecked="";if(checkbox.attr("type")=="checkbox"){isChecked=$("#"+checkbox.attr("id")+":checked").length>0}else if("test"=="test"){var targetValue=checkbox.data("target-value");if(checkbox.val()==targetValue){isChecked=true}}if(isChecked){checkbox.closest(".novelist-row").nextAll(target).slideDown()}else{checkbox.closest(".novelist-row").nextAll(target).slideUp()}}function checkValueChange(){$(".novelist-checkbox-change").each(function(){triggerChange($(this),$(this).data("target"));$(this).change(function(){triggerChange($(this),$(this).data("target"))})})}checkValueChange();$(document).on("widget-updated widget-added",function(e,widget){checkValueChange()})})(jQuery);